import Table from './Table'
import React, { useState, useEffect } from 'react';

function CreateDetails(props) {
  function handleDeleteDetail(id) {
    fetch(`http://localhost/api/contacts/${props.contactId}/phones/${id}`, {
      method: 'DELETE'
    })
      .then(response => response.json())
      .then(() => {
        props.setPhones(phones => phones.filter(phone => phone.id !== id));
      })
      .catch(error => console.error('Error:', error));
  }

  return (
    <tr>
      <td>{props.type}</td>
      <td>{props.number}</td>
      <td>
        <button className="button2" onClick={() => handleDeleteDetail(props.id)}>
          Delete
        </button>
      </td>
    </tr>
  );
}

function CreateContact(props) {
  const [phones, setPhones] = useState([]);
  const [newNum, setNumber] = useState('');
  const [newType, setType] = useState('');
  useEffect(() => {
    fetch(`http://localhost/api/contacts/${props.id}/phones`)
      .then(response => response.json())
      .then(data => setPhones(data))
      .catch(error => {
        console.error('Error:', error);
      });
  }, [props.id]); 

  // const handleAddDetail = () => {
  //   const newPhone = { id: phones.length + 1, type: newType, number: newNum };
  //   setPhones([...phones, newPhone]);
  //   setType('');
  //   setNumber('');
  // };
  const handleAddDetail = () => {
    const contactId = props.id;
    const phoneId = newNum;
    fetch(`http://localhost/api/contacts/${contactId}/phones`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({type: newType, number: newNum, id: phoneId })
    })
      .then(response => response.json())
      .then(data => {
        // Update the parent component's state with the new phone
        props.setPhones([...phones, data]);
      })
      .catch(error => console.error('Error:', error));

    setType('');
    setNumber('');
  };

  function handleTypeChange(event) {
    setType(event.target.value);
  }

  function handleNumberChange(event) {
    setNumber(event.target.value);
  }

  return (
    <div>
      <h3>{props.name}</h3>
      <input
        type="text"
        placeholder="Type:"
        onChange={handleTypeChange}
        value={newType}
      />
      <input
        type="text"
        placeholder="Number:"
        value={newNum}
        onChange={handleNumberChange}
      />
      <button className="button1" onClick={handleAddDetail}>
        Add
      </button>

      <table>
        <thead>
          <tr>
            <th>
              <h4>Type</h4>
            </th>
            <th>
              <h4>Number</h4>
            </th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {phones.map(phone => (
            <CreateDetails
              key={phone.id}
              setPhones={props.setPhones}
              contactId={props.id}
              id={phone.id}
              type={phone.type}
              number={phone.number}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default CreateContact;
