                <button class = "button1" onClick={handleButtonClick}>Create Contact</button>    
           </div>   
           {/* <CreateContact name = {inputValue}/>   */}

           {props.contacts.map(contact => <Detail setContacts={props.setContacts} id={contact.id} contact_name={contact.contact_name}/>) }



