import Table from './Table'
import React, { useState, useEffect } from 'react';

function CreateDetails(props) {
  // Find the Detail we want to delete and remove it
  function handleDeleteDetail(id) {
    props.setPhones(phones => phones.filter(phone => phone.id !== id));
  }

  return (
    <tr key={props.id}>
      <td>{props.type}</td>
      <td>{props.number}</td>
      <td>
        <button className="button2" onClick={() => handleDeleteDetail(props.id)}>
          Delete
        </button>
      </td>
    </tr>
  );
}

function CreateContact(props){

  console.log(props);
  const [phones, setPhones] = useState([]);
  const [newNum, setNumber] = useState('');
  const [newType, setType] = useState('');
  const [idCounter, setIdCounter] = useState(1);

  useEffect(() => {
    fetch(`http://localhost/api/contacts/${props.id}/phones/`)
    .then(response => response.json())
    .then(data => setPhones(data))
    .catch((error) => {
    console.error('Error:', error);
    });
}, []);

  const handleAddDetail = () => {
    // const newPhone = {id: idCounter, type: newType, number: newNum};
    // setPhones([...phones, newPhone]);
    const updatePhone = {
      id: props.id, 
      name: props.name,
      type: newType, 
      number: newNum
    }

		fetch(`http://localhost/api/contacts/${props.id}/phones/`, {
			method: 'POST',
			headers: {
			'Content-Type': 'application/json'
			},
			body: JSON.stringify(updatePhone)
		})
		.then(response => response.json())
    // .then(data => {
		// 	props.setPhones(phones => [...phones, data]);
		// })
		.then(() => {
			props.setPhones(phones => phones.map(phone => {
				if (phone.id === props.id) {
					return updatePhone;
				} else {
					return phone;
				}
			}));
		})
		.catch((error) => {
			console.error('Error:', error);
		});
    setType('');
    setNumber('');
    setIdCounter(idCounter + 1);
  };

  function handleTypeChange(event) {
    setType(event.target.value);
  };
  function handleNumberChange(event){
    setNumber(event.target.value)
  };


    return(
    <div>
        <h3>{props.name}</h3>
        <input type ="text" 
          placeholder="Type:" 
          onChange={handleTypeChange}
          value = {newType}/>

        <input type = "text" 
          placeholder="Number:" 
          value={newNum}
          onChange={handleNumberChange}/>

        <button class = "button1" onClick={handleAddDetail}>Add</button>

        <table>
        <thead>
          <tr>
            <th>
              <h4>Type</h4>
            </th>
            <th>
              <h4>Number</h4>
            </th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {phones.map(phone => (
            <CreateDetails
              key={phone.id}
              setPhones={props.setPhones}
              id={phone.id}
              type={phone.type}
              number={phone.number}
            />
          ))}
        </tbody>
      </table>

            {/* <Table id={phone.id} type={phone.type} number={phone.number} />
            <button className="button2" onClick={() => handleDeleteDetail(phone.id)}>Delete</button> */}

    </div>
    );
  }

export default CreateContact;
