function Create(props) {
    return(
        <div style={{border: "solid", padding:"10px"}} id = "new_contact">
            let x = document.getElementById({props.inputID});
            let v = x.value
            document.getElementById("new_name").innerHTML = v; 
        </div>
    );
}
function Show(props){
    <p>{props.status}</p>
}



function Add(props) {
    return (
        <div >
           <div style={{border: "double", padding:"10px"}}>
           <h2>{props.heading}</h2>
           <input type = "text" id = "name"/> <br/><br/>
           <button onClick= "create()">Create Contact</button>
           <p>------------------------------------------------------</p>
           <h1 id = "new_name"></h1>
           <script>
            <Create inputID = "name" />
           </script>
           </div>

           <div>
            <Show id = "asso_number" status = "Click a contact to view associated phone numbers"/>
            <Show id = "stat" status = "Show Stats"/>
           </div>

        </div>

    );
}

export default Add;
