function Add(props) {
    const [inputValue, setInputValue] = useState('');

    const handleInputChange = (event) => {setInputValue(event.target.value);
    };

    const handleButtonClick = () => {
    CreateContact();  // Clear the input value after passing to CreateContact
    };
    return (
        <div >
           <div style={{borderBottom: "solid white", padding:"10px", backgroundColor: "#c1d3fe"}}>
           <h2>{props.heading}</h2>
           <input type = "text" id = "name"
                  value={inputValue}
                  onChange={handleInputChange}/> 
           <br/><br/>
           <button id ="button_create" class = "button1" onClick={handleButtonClick}>
           <p>Create Contact</p>
           </button>    


           </div>   
           <CreateContact name = {inputValue}/>  


        </div>

    );
}

export default Add;
