import React, { useState, useEffect } from 'react';
import CreateContact from './CreateContact';

function Add(props) {
  console.log(props);
  const [contacts, setContacts] = useState([]);
  const [newName, setNewName] = useState('');
  const [phones, setPhones] = useState([]);
  useEffect(() => {
    fetch(`http://localhost/api/contacts/${props.id}/phones`)
      .then(response => response.json())
      .then(data => setPhones(data))
      .catch(error => {
        console.error('Error:', error);
      });
  }, [props.id]); 


  const handleAddContact = () => {
    // const newContact = { name: newName, id: newName.length + 1 };  // Modify this with actual input values
    // setContacts([...contacts, newContact]);
    // setNewName('');
    const newContact = {
      name: newName,
      phones: phones,  // Send the phones data here
    };

    //call apis
    fetch('http://localhost/api/contacts', {
			method: 'POST',
			headers: {
			'Content-Type': 'application/json'
			},
			body: JSON.stringify(newContact)
			})
			.then(response => response.json())
			.then(data => {
			props.setContacts(contacts => [...contacts, data]);
			})
			.catch((error) => {
			console.error('Error:', error);
			});
    setNewName('');
  };

  function handleInputChange(event) {
    setNewName(event.target.value);
  };

  function handleDeleteDetail(id) {
    fetch(`http://localhost/api/contacts/${id}`, {
      method: 'DELETE',
    })
      .then(() => {
        props.setContacts(contacts => contacts.filter(contact => contact.id !== id));
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };


  return (
    <div>
      <div style={{ borderBottom: 'solid white', padding: '10px', backgroundColor: '#7D7C7C' }}>
        <h2>{props.heading}</h2>
        <input type="text" 
               placeholder="Name" 
               value = {newName}
               onChange={handleInputChange}

        />
        <br /><br />
        <button
          className="button1"
          onClick={handleAddContact}
        >
          <p>Create Contact</p>
        </button>
      </div>

{/* //problem with contactid here */}
      {contacts.map((contact) => (
        <div key={contact.id} className='CreateContact'>
            <CreateContact setPhones = {setPhones}
                           phones = {contact.phones}
                           id = {contact.id} 
                           name={contact.name}/>
            <button className="button2" 
                    style={{ width: "30%"}} 
                    onClick={() => handleDeleteDetail(contact.id)}>Delete Contact</button>
        </div>
      ))}
    </div>
  );
}

export default Add;
