import Table from './Table'
import React, { useState, useEffect } from 'react';

function CreateDetails(props) {
  // Find the Detail we want to delete and remove it
  function handleDeleteDetail(id) {
    props.setPhones(phones => phones.filter(phones => phones.id !== id));
  }

  return (
    <tr key={props.id}>
      <td>{props.type}</td>
      <td>{props.number}</td>
      <td>
        <button className="button2" onClick={() => handleDeleteDetail(props.id)}>
          Delete
        </button>
      </td>
    </tr>
  );
}

function CreateContact(props) {
  const [phones, setPhones] = useState([]);
  const [newNum, setNumber] = useState('');
  const [newType, setType] = useState('');

  const handleAddDetail = () => {
    const newPhone = { id: phones.length + 1, type: newType, number: newNum };
    setPhones([...phones, newPhone]);
    setType('');
    setNumber('');
  };

  function handleTypeChange(event) {
    setType(event.target.value);
  }

  function handleNumberChange(event) {
    setNumber(event.target.value);
  }

  return (
    <div>
      <h3>{props.name}</h3>
      <input
        type="text"
        placeholder="Type:"
        onChange={handleTypeChange}
        value={newType}
      />
      <input
        type="text"
        placeholder="Number:"
        value={newNum}
        onChange={handleNumberChange}
      />
      <button className="button1" onClick={handleAddDetail}>
        Add
      </button>

      <table>
        <thead>
          <tr>
            <th>
              <h4>Type</h4>
            </th>
            <th>
              <h4>Number</h4>
            </th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {phones.map(phone => (
            <CreateDetails
              key={phone.id}
              setPhones={setPhones}
              id={phone.id}
              type={phone.type}
              number={phone.number}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
}

