import { useState, useEffect } from 'react';  // import useEffect
import Create from './Create'

function Show(props){
    return(
    <p>{props.status}</p>
    );
}

function Add(props) {
    useEffect(() => { <Create id = "name"/>; }
        );
    return (
        <div >
           <div style={{border: "double", padding:"10px"}}>
           <h2>{props.heading}</h2>
           <input type = "text" id = "name"/> <br/><br/>
           <button id ="button_create" onClick={Create()}>Create Contact</button>
           <h3 id ="name_created"></h3>
           </div>


           <div>
            <Show id = "asso_number" status = "Click a contact to view associated phone numbers"/>
            <Show id = "stat" status = "Show Stats"/>
           </div>

        </div>

    );
}

export default Add;
