import Table from './Table'
import React, { useState } from 'react';

function CreateContact(props){

//   console.log(props);
  const [details, setDetails] = useState([]);
  const [newNum, setNumber] = useState('');
  const [newType, setType] = useState('');

  const handleAddDetail = () => {
    const newDetails = {type: {newType}, number: {newNum}};  // Modify this with actual input values
    setDetails([...details, newDetails]);
    setType('');
    setNumber('');
  };

  function handleTypeChange(event) {
    setType(event.target.value);
  };
  function handleNumberChange(event){
    setNumber(event.target.value)
  }

    return(
    <div class = "CreateContact">
        <h3>{props.name}</h3>


        Type: <input type ="text" id = "type" onChange={handleTypeChange}/>
        Number:  <input type = "text" id = "number" onChange={handleNumberChange}/>
        <button class = "button1" onClick={handleAddDetail}>Add</button>

        {details.map((detail) => (
        <Table type = {detail.newType} number ={detail.newNum}/> ))}

        <br/>
        <button id = "dl" class = "button2">Delete</button> 

    </div>
    );
  }

export default CreateContact;
