import React, { useState } from 'react';
import CreateContact from './CreateContact';

function Add(props) {
  const [contacts, setContacts] = useState([]);

  const handleAddContact = () => {
    const newContact = { name: '', type: '', number: '' };  // Modify this with actual input values
    setContacts([...contacts, newContact]);
  };

  return (
    <div>
      <div style={{ borderBottom: 'solid white', padding: '10px', backgroundColor: '#c1d3fe' }}>
        <h2>{props.heading}</h2>
        <input type="text" id="name" placeholder="Name" />
        <br /><br />
        <button
          id="button_create"
          className="button1"
          onClick={handleAddContact}
        >
          <p>Create Contact</p>
        </button>
      </div>

      {contacts.map((contact, index) => (
        <CreateContact key={index} name={contact.name} />
      ))}
    </div>
  );
}

export default Add;
