import Table from './Table'
import React, { useState } from 'react';

function CreateContact(props){

//   console.log(props);
  const [details, setDetails] = useState([]);
  const [newNum, setNumber] = useState('');
  const [newType, setType] = useState('');

  const handleAddDetail = () => {
    const newDetails = {id: details.length + 1, type: newType, number: newNum};
    setDetails([...details, newDetails]);
    setType('');
    setNumber('');
  };

  function handleTypeChange(event) {
    setType(event.target.value);
  };
  function handleNumberChange(event){
    setNumber(event.target.value)
  };
  // Find the task we want to delete and remove it
  function handleDeleteDetail(id){
    props.setDetails(details => details.filter(detail => detail.id !== props.id))
  };

    return(
    <div class = "CreateContact">
        <h3>{props.name}</h3>
        <input type ="text" 
          placeholder="Type:" 
          onChange={handleTypeChange}
          value = {newType}/>

        <input  type = "text" 
          placeholder="Number:" 
          value={newNum}
          onChange={handleNumberChange}/>

        <button class = "button1" onClick={handleAddDetail}>Add</button>

        {details.map((detail) => (
        <Table id = {detail.id} type = {detail.type} number ={detail.number}/> ))}

        <br/>
        <button id = "dl" class = "button2">Delete</button> 

    </div>
    );
  }

export default CreateContact;
