import Table from './Table'
import React, { useState } from 'react';

function CreateContact(props){

  console.log(props);
  const [details, setDetails] = useState([]);
  const [newNum, setNumber] = useState('');
  const [newType, setType] = useState('');
  const [idCounter, setIdCounter] = useState(1);


  const handleAddDetail = () => {
    const newDetails = {id: idCounter, type: newType, number: newNum};
    setDetails([...details, newDetails]);
    setType('');
    setNumber('');
    setIdCounter(idCounter + 1);
  };

  function handleTypeChange(event) {
    setType(event.target.value);
  };
  function handleNumberChange(event){
    setNumber(event.target.value)
  };
  // Find the Detail we want to delete and remove it
  function handleDeleteDetail(id){
    setDetails(details => details.filter(detail => detail.id !== id))
  };

    return(
    <div>
        <h3>{props.name}</h3>
        <input type ="text" 
          placeholder="Type:" 
          onChange={handleTypeChange}
          value = {newType}/>

        <input type = "text" 
          placeholder="Number:" 
          value={newNum}
          onChange={handleNumberChange}/>

        <button class = "button1" onClick={handleAddDetail}>Add</button>

        <table>
        <thead>
          <tr>
            <th><h4>Type</h4></th>
            <th><h4>Number</h4></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {details.map((detail) => (
            <tr key={detail.id}>
              <td>{detail.type}</td>
              <td>{detail.number}</td>
              <td>
                <button className="button2" onClick={() => handleDeleteDetail(detail.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
            {/* <Table id={detail.id} type={detail.type} number={detail.number} />
            <button className="button2" onClick={() => handleDeleteDetail(detail.id)}>Delete</button> */}




    </div>
    );
  }

export default CreateContact;
